import environment from './../environment/environment.js';

export const fetchGlobalData =  async () => {
    let api = await fetch(environment.urlGlobal);
    const {data :{active, deaths, recovered, cases} }  = await api.json();
    return data;
}

export const fetchCountryData =  async () => {
    let response = await fetch(environment.allCountries);
    return response.json();
}
