import React, { useState }from 'react';
import styled from '@emotion/styled';
import AnimatedNumber from 'animated-number-react';
import moment from 'moment';

const CardStyled = styled.div`
    margin: 5px;
    background-color : #e0e0e0;
    display: flex;
    flex-direction: column;
    max-width: 300px;
    min-width: 300px;
    padding: 16px;
    border-bottom: 5px solid ${props => props.color || 'turquoise'};
    border-top-left-radius: 50px 20px;
    box-shadow: 0px 2px 1px -1px rgba(0,0,0,0.2),0px 1px 1px 0px rgba(0,0,0,0.14),0px 1px 3px 0px rgba(0,0,0,0.12);
`;

const Icon = styled.i`
    color: ${props => props.color || 'inherit'};
    margin-right: 5px;
`;

const Title = styled.div`
    font-size: 18px;
`;


const Card = ({color, type}) => {

    const [numero, setNumber] = useState(0);

    const formatValue = (number) => new Intl.NumberFormat("es-ES").format(number);

    const setValue = (e) => {

        setNumber(e.target.value);
    }

    return (
    <CardStyled color={color}>
        <Title>
            <Icon color={color} className="fas fa-virus"></Icon>                
            <span>{type}</span>
        </Title>
        <AnimatedNumber 
            value={numero}
            formatValue={formatValue}
            duration={300}/>
        <span>{moment().format('ll')}</span>
        <input 
            type="number"
            name="num"
            onChange={setValue}  
            value={numero}          
            />
    </CardStyled>
    );
}

export default Card;
