import React from 'react';
import TextField from '@material-ui/core/TextField';
import Autocomplete from '@material-ui/lab/Autocomplete';
import countries from './../constant/countries.js';
import styled from '@emotion/styled';


const SelecStyled = styled.div`
    width : 250px;
    margin: 25px auto;
`;

const Select = () => {

    const isEmoji = (str) => {
        var ranges = [
            '(?:[\u2700-\u27bf]|(?:\ud83c[\udde6-\uddff]){2}|[\ud800-\udbff][\udc00-\udfff]|[\u0023-\u0039]\ufe0f?\u20e3|\u3299|\u3297|\u303d|\u3030|\u24c2|\ud83c[\udd70-\udd71]|\ud83c[\udd7e-\udd7f]|\ud83c\udd8e|\ud83c[\udd91-\udd9a]|\ud83c[\udde6-\uddff]|[\ud83c[\ude01-\ude02]|\ud83c\ude1a|\ud83c\ude2f|[\ud83c[\ude32-\ude3a]|[\ud83c[\ude50-\ude51]|\u203c|\u2049|[\u25aa-\u25ab]|\u25b6|\u25c0|[\u25fb-\u25fe]|\u00a9|\u00ae|\u2122|\u2139|\ud83c\udc04|[\u2600-\u26FF]|\u2b05|\u2b06|\u2b07|\u2b1b|\u2b1c|\u2b50|\u2b55|\u231a|\u231b|\u2328|\u23cf|[\u23e9-\u23f3]|[\u23f8-\u23fa]|\ud83c\udccf|\u2934|\u2935|[\u2190-\u21ff])' // U+1F680 to U+1F6FF
        ];
        if (str.match(ranges.join('|'))) {
            return true;
        } else {
            return false;
        }
    }

    const countryToFlag = (isoCode) => {
        if (isEmoji(isoCode)) {
            return isoCode;
        } else {            
            return typeof String.fromCodePoint !== 'undefined'
            ? isoCode
                .toUpperCase()
                .replace(/./g, (char) => String.fromCodePoint(char.charCodeAt(0) + 127397))
            : isoCode;
        }
    }

    const defaultProps = {
        options: countries,
        getOptionLabel: (option) => countryToFlag(option.code)  + option.label,
    };

    return (
        <SelecStyled>
            <Autocomplete
                {...defaultProps}
                id="Contries"                
                renderInput={(params) => <TextField {...params} label="Contries" margin="normal" 
                />}
                onChange={(event, value) => setCountry}
            />
        </SelecStyled>
    );
}

export default Select;
