import React, { Fragment, useEffect, useState } from 'react';
import Header from './components/Header.js';
import Card from './components/Card.js';
import Select from './components/Select.js';
import styled from '@emotion/styled';
import '@fortawesome/fontawesome-free/css/all.min.css';
import {fetchGlobalData, fetchCountryData} from './api';
import {isEmoji} from './helpers/helpers.js';
import { Line } from 'react-chartjs-2';

const Container = styled.div`
    margin: auto;
    width: 95%;    
`;

const CardWrapper = styled.div`
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
`;

const ChartWrapper = styled.div`
    width: 50%;
`;

function App() {

    const [country, setCountry] = useState({ code: 'ðŸŒŽ', label: 'Global'  });
    const [loading, setLoading] = useState(true);
    const [state, setstate] = useState({
        active: 0,
        cases: 0,
        deaths: 0,
        recovered: 0
    });

    const {active, cases, deaths, recovered} = state;

    const getGlobalData = async () => {
        let response = await fetchGlobalData();
        setLoading(false);
        setstate(response);        
    }
    const getCountryData = async (countryCode) => {
        let response = await fetchCountryData(countryCode);
        setLoading(false);
        setstate(response);        
    }

    useEffect(() => {
        if (isEmoji(country.code)){
            getGlobalData();
        } else {
            getCountryData(country.code);
        }             
    }, [country]);

    const data = {
        labels: ['January', 'February', 'March', 'April', 'May', 'June', 'July'],
        datasets: [{
            label: 'My First dataset',
            backgroundColor: 'rgb(255, 99, 132)',
            borderColor: 'rgb(255, 99, 132)',
            data: [0, 10, 5, 2, 20, 30, 45]
        }]
    }

    return (
        <Fragment>
            <Header></Header> 
            <Container>
                <Select setCountry={setCountry} country={country} setLoading={setLoading}></Select>
                <CardWrapper>
                    <Card color={'#2eb2ff'} type={'Diagnosed'} numberCases={cases} loading={loading}></Card>   
                    <Card color={'#008037'} type={'Recovered'} numberCases={recovered} loading={loading}></Card>   
                    <Card color={'#ff5c5c'} type={'Deceased'} numberCases={deaths} loading={loading}></Card>   
                    <Card color={'#ff914d'} type={'Actives'} numberCases={active} loading={loading}></Card>       
                </CardWrapper> 
                <ChartWrapper>
                    <Line 
                        width={100}
                        height={50}
                        data={data} 
                        options={{ maintainAspectRatio: true }}/>                          

                </ChartWrapper>
            </Container>
        </Fragment>
    );
}

export default App;
