import TextField from '@material-ui/core/TextField';
import Autocomplete from '@material-ui/lab/Autocomplete';
import countries from './../constant/countries.js';
import styled from '@emotion/styled';
import { countryToFlag } from './../helpers/helpers.js';


const SelecStyled = styled.div`
    width : 250px;
    margin: 0px auto 25px;
`;

const Select = ({setCountry}) => {

    const defaultProps = {
        options: countries,
        getOptionLabel: (option) => countryToFlag(option.code)  + option.label,
    };

    const onChangeCountry = (event, value) =>{
        const {code, label} = value;
        if (code.trim() !== '' && value.trim() !== '') {
            setCountry(value);
        }    
    } 

    return (
        <SelecStyled>
            <Autocomplete
                {...defaultProps}
                id="Contries"
                placeholder='Global'
                value={countries[0]}                                               
