import React, { Fragment, useEffect, useState } from 'react';
import Header from './components/Header.js';
import Card from './components/Card.js';
import Select from './components/Select.js';
import styled from '@emotion/styled';
import '@fortawesome/fontawesome-free/css/all.min.css';
import {fetchGlobalData, fetchCountryData} from './api';


const Container = styled.div`
    margin: auto;
    width: 95%;    
`;

const CardWrapper = styled.div`
    display: flex;
    flex-wrap: wrap;
    justify-content: space-evenly;
`;




function App() {

    const [state, setstate] = useState({
        active: 0,
        cases: 0,
        deaths: 0,
        recovered: 0
    });

    useEffect(async () => {
        let response = await fetchGlobalData();
        console.log(response);
    });



    return (
        <Fragment>
            <Header></Header> 
            <Container>
                <Select></Select>
                <CardWrapper>
                    <Card color={'#2eb2ff'} type={'Diagnosed'}></Card>   
                    <Card color={'#008037'} type={'Recovered'}></Card>   
                    <Card color={'#ff5c5c'} type={'Deceased'}></Card>   
                    <Card color={'#ff914d'} type={'Estimated'}></Card>       
                </CardWrapper>            
            </Container>
        </Fragment>
    );
}

export default App;
