import countries from './../constant/countries.js';
import styled from '@emotion/styled';
import {isEmoji, countryToFlag} from './../helpers/helpers.js';


const SelecStyled = styled.div`
    width : 250px;
    margin: 25px auto;
`;

const Select = () => {



    const defaultProps = {
        options: countries,
        getOptionLabel: (option) => countryToFlag(option.code)  + option.label,
    };

    return (
        <SelecStyled>
            <Autocomplete
                {...defaultProps}
                id="Contries"                
                renderInput={(params) => <TextField {...params} label="Contries" margin="normal" 
                />}
                onChange={(event, value) => setCountry}
            />
        </SelecStyled>
    );
}

export default Select;
