import React, { Fragment } from 'react';
import Header from './components/Header.js';
import Card from './components/Card.js';
import styled from '@emotion/styled';
import TextField from '@material-ui/core/TextField';
import Autocomplete from '@material-ui/lab/Autocomplete';
import '@fortawesome/fontawesome-free/css/all.min.css';
import countries from './constant/countries.js';

const Container = styled.div`
    display: flex;
    flex-wrap: wrap;
    justify-content: space-evenly;
    margin: auto;
    width: 95%;    
`;


function countryToFlag(isoCode) {
    return typeof String.fromCodePoint !== 'undefined'
      ? isoCode
          .toUpperCase()
          .replace(/./g, (char) => String.fromCodePoint(char.charCodeAt(0) + 127397))
      : isoCode;
  }
  const defaultProps = {
    options: countries,
    getOptionLabel: (option) => countryToFlag(option.code)  + option.label,
  };
function App() {
  return (
    <Fragment>
        <Header></Header> 
        <Container>
            <Autocomplete
                {...defaultProps}
                id="Contries"
                debug
                renderInput={(params) => <TextField {...params} label="Contries" margin="normal" />}
            />          
            <Card color={'#2eb2ff'}></Card>   
            <Card color={'#008037'}></Card>   
            <Card color={'#ff5c5c'}></Card>   
            <Card color={'#ff914d'}></Card>   
        </Container>
    </Fragment>
  );
}

export default App;
